insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("C++ Templates (第2版 英文版)", "https://img9.doubanio.com/view/subject/l/public/s29925490.jpg", 10.0, 13.0, "本书是同名经典畅销图书的全新升级版本，针对C++11、C++14、C++17标准进行了全面更新，并对所有的新语言特性（包括可变参数模板、通用lambda、类模板参数推导等）进行了解释。全书共28章。首先全面介绍了本书的内容结构和相关情况。第1部分（第1～11章）介绍了模板的基本概念，第2部分（第12～17章）深入阐述了模板的细节，第3部分（第18～28章）介绍了C++模板所支持的基本设计技术。附录", 10, "朱子杰",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("ES6标准入门", "https://img9.doubanio.com/view/subject/l/public/s33597067.jpg", 8.8, 20.99, "ES6是下一代JavaScript语言标准的统称，每年6月发布一次修订版，迄今为止已经发布了3个版本，分别是ES2015、ES2016、ES2017。本书根据ES2017标准，详尽介绍了所有新增的语法，对基本概念、设计目的和用法进行了清晰的讲解，给出了大量简单易懂的示例。本书为中级难度，适合那些已经对JavaScript语言有一定了解的读者，可以作为学习这门语言最新进展的工具书，也可以作为参考手册", 6, "刘宛晴",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("WebGL编程指南", "https://img9.doubanio.com/view/subject/l/public/s27306770.jpg", 8.9, 1.99, "WebGL 是一项在网页上渲染三维图形的技术，也是HTML5 草案的一部分。《WebGL编程指南》的主要篇幅讲解了WebGL 原生API 和三维图形学的基础知识，包括渲染管线、着色器、矩阵变换、着色器编程语言（GLSL ES）等等，也讲解了使用WebGL 渲染三维场景的一般技巧，如光照、阴影、雾化等等。《WebGL编程指南》提供了丰富的示例程序供读者钻研，也提供了极具价值的附录供读者参考。《Web", 9, "李大钊",100);
