insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("C++ Primer Plus", "https://img9.doubanio.com/view/subject/l/public/s10307483.jpg", 8.5, 13.0, "C++是在C语言基础上开发的一种集面向对象编程、通用编程和传统的过程化编程于一体的编程语言，是C语言的超集。本书是根据2003年的ISO/ANSI C++标准编写的。通过大量短小精悍的程序详细而全面地阐述了C++的基本概念和技术。全书分为18章和10个附录，分别介绍了C++程序的运行方式、基本数据类型、复合数据类型、循环和关系表达式、分支语句和逻辑操作符、函数重载和函数模板、内存模型和名称空间、类", 2, "金浩然",100);
