insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("C#并发编程经典实例", "https://img9.doubanio.com/view/subject/l/public/s29059804.jpg", 8.0, 19.8, "本书全面讲解C#并发编程技术，侧重于.NET平台上较新、较实用的方法。全书分为几大部分：首先介绍几种并发编程技术，包括异步编程、并行编程、TPL数据流、响应式编程；然后阐述一些重要的知识点，包括测试技巧、互操作、取消并发、函数式编程与OOP、同步、调度；最后介绍了几个实用技巧。全书共包含70多个有配套源码的实用方法，可用于服务器程序、桌面程序和移动应用的开发。本书适合具有.NET基础，希望学习最新", 1, "石头",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("数据结构与算法：python语言实现", "https://img9.doubanio.com/view/subject/l/public/s29867312.jpg", 6.4, 22.99, "本书采用Python语言讨论数据结构和算法，详细讲解其设计、分析与实现过程，是一本内容全面且特色鲜明的教材。书中将面向对象视角贯穿始终，充分利用Python语言优美而简洁的特点，强调代码的健壮性和可重用性，关注各种抽象数据类型以及不同算法实现策略的权衡。", 4, "张跃平",100);
