insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("剑指Offer：名企面试官精讲典型编程题（第2版）", "https://img9.doubanio.com/view/subject/l/public/s29413793.jpg", 8.8, 25.0, "《剑指Offer：名企面试官精讲典型编程题（第2版）》剖析了80个典型的编程面试题，系统整理基础知识、代码质量、解题思路、优化效率和综合能力这5个面试要点。《剑指Offer：名企面试官精讲典型编程题（第2版）》共分7章，主要包括面试的流程，讨论面试每一环节需要注意的问题；面试需要的基础知识，从编程语言、数据结构及算法三方面总结程序员面试知识点；高质量的代码，讨论影响代码质量的3个要素（规范性、完整", 7, "李大钊",100);
