insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("贝叶斯方法", "https://img9.doubanio.com/view/subject/l/public/s29200427.jpg", 8.5, 39.99, "贝叶斯推理的方法非常自然和极其强大。然而，大多数图书讨论贝叶斯推理，依赖于非常复杂的数学分析和人工的例子，使没有强大数学背景的人无法接触。不过，现在好了，卡梅伦的这本书从编程、计算的角度来介绍贝叶斯推理，把贝叶斯理论和编程实践结合起来，使大多数程序员都可以入门并掌握。本书通过强大的Python语言库PyMC，以及相关的Python工具，包括NumPy\SciPy\Matplotlib讲解了概率编程", 4, "朱子杰",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("PHP7内核剖析", "https://img9.doubanio.com/view/subject/l/public/s29607420.jpg", 8.4, 20.99, "PHP作为最流行的语言之一，自第一个版本发布至今的二十几年里经历了多次重大改进，PHP7版本最大的特色在于其性能上的突破，比PHP5快了一倍。目前PHP7已经得到了广泛应用，越来越多的项目从PHP5迁移到了PHP7。目前，关于PHP内核的资料非常有限，《PHP7内核剖析》以当前最为流行的PHP7版本为基础，系统性地、尽可能详细地介绍PHP语言底层的实现，旨在帮助更多的开发者进一步理解PHP，参与到", 7, "鲁迅",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("图解Java多线程设计模式", "https://img9.doubanio.com/view/subject/l/public/s29520124.jpg", 8.7, 24.99, "本书通过具体的Java 程序，以浅显易懂的语言逐一说明了多线程和并发处理中常用的12 种设计模式。内容涉及线程的基础知识、线程的启动与终止、线程间的互斥处理与协作、线程的有效应用、线程的数量管理以及性能优化的注意事项等。此外，还介绍了一些多线程编程时容易出现的失误，以及多线程程序的阅读技巧等。在讲解过程中，不仅以图配文，理论结合实例，而且提供了运用模式解决具体问题的练习题和答案，帮助读者加深对多线", 10, "刘宛晴",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("PHP 7底层设计与源码实现", "https://img9.doubanio.com/view/subject/l/public/s32315965.jpg", 7.8, 12.49, "本书分为四大部分，共计14章内容。第1篇（第1～2章）主要介绍PHP7的新特性，以及研读源码的方法、PHP7源码结构等。第2篇（第3～6章）会详细介绍PHP7的变量实现，涵盖基本变量zval的结构，字符串zend_string的实现，PHP7中的核心结构HashTable的实现，类和对象的实现。第3篇（第7～11章）介绍PHP7核心的实现，涵盖PHP7的生命周期及其模块初始化阶段中配置文件的解析，", 7, "魔方",100);
