insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("跟老齐学Python", "https://img9.doubanio.com/view/subject/l/public/s28504762.jpg", 7.2, 12.0, "《跟老齐学Python：从入门到精通》是面向编程零基础读者的Python入门教程，内容涵盖了Python的基础知识和初步应用。以比较轻快的风格，向零基础的学习者介绍一门时下比较流行、并且用途比较广泛的编程语言，所以，《跟老齐学Python：从入门到精通》读起来不晦涩，并且在其中穿插了很多貌似与Python编程无关，但与学习者未来程序员职业生涯有关的内容。《跟老齐学Python：从入门到精通》特别强", 5, "猪猪",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("实现领域驱动设计", "https://img9.doubanio.com/view/subject/l/public/s29544670.jpg", 8.5, 1.99, "领域驱动设计（DDD）是教我们如何做好软件的，同时也是教我们如何更好地使用面向对象技术的。它为我们提供了设计软件的全新视角，同时也给开发者留下了一大难题：如何将领域驱动设计付诸实践？Vaughn Vernon 的这本《实现领域驱动设计》为我们给出了全面的解答。《实现领域驱动设计》分别从战略和战术层面详尽地讨论了如何实现DDD，其中包含了大量的最佳实践、设计准则和对一些问题的折中性讨论。《实现领域驱", 2, "鲁迅",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Python数据可视化编程实战", "https://img9.doubanio.com/view/subject/l/public/s28060713.jpg", 7.2, 19.99, "《Python数据可视化编程实战》是一本使用Python实现数据可视化编程的实战指南，介绍了如何使用Python最流行的库，通过60余种方法创建美观的数据可视化效果。全书共8章，分别介绍了准备工作环境、了解数据、绘制并定制化图表、学习更多图表和定制化、创建3D可视化图表、用图像和地图绘制图表、使用正确的图表理解数据以及更多matplotlib知识。《Python数据可视化编程实战》适合那些对Pyt", 7, "安徒生",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("漫画算法：小灰的算法之旅（Python篇）", "https://img9.doubanio.com/view/subject/l/public/s33623894.jpg", 8.5, 12.49, "《漫画算法》通过虚拟的主人公小灰的心路历程，用漫画的形式讲述了算法和数据结构的基础知识、复杂多变的算法面试题目及算法的实际应用场景。第1章介绍了算法和数据结构的相关概念，告诉大家算法是什么，数据结构又是什么，它们有哪些用途，如何分析时间复杂度，如何分析空间复杂度。第2章介绍了最基本的数据结构，包括数组、链表、栈、队列、哈希表的概念和读写操作。第3章介绍了树和二叉树的概念、二叉树的各种遍历方式、二叉", 5, "成超",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("labuladong的算法小抄", "https://img9.doubanio.com/view/subject/l/public/s33762849.jpg", 6.8, 12.49, "《labuladong的算法小抄》专攻算法刷题，训练算法思维，应对算法笔试。注重用套路和框架思维解决问题，以不变应万变。　　第1章列举了几个最常见的算法类型及对应的解题框架思路，包括动态规划、回溯、广度优先搜索及双指针、滑动窗口等算法技巧。　　第2章用动态规划的通用思路框架解决了十几道经典的动态规划问题，例如，正则表达式、背包问题，同时还介绍了如何写状态转移方程、如何进行状态压缩等技巧。　　第3章", 7, "魔方",100);
