insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("MySQL技术内幕", "https://img9.doubanio.com/view/subject/l/public/s29731159.jpg", 8.5, 25.0, "《MySQL技术内幕:InnoDB存储引擎(第2版)》由国内资深MySQL专家亲自执笔，国内外多位数据库专家联袂推荐。作为国内唯一一本关于InnoDB的专著，《MySQL技术内幕:InnoDB存储引擎(第2版)》的第1版广受好评，第2版不仅针对最新的MySQL 5.6对相关内容进行了全面的补充，还根据广大读者的反馈意见对第1版中存在的不足进行了完善，《MySQL技术内幕:InnoDB存储引擎(第2", 4, "曹文轩",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("垃圾回收的算法与实现", "https://img9.doubanio.com/view/subject/l/public/s28830934.jpg", 8.3, 24.99, "★ Ruby之父Matz作推荐语：上古传承的魔法，彻底揭开垃圾回收的秘密！★ 日本天才程序员兼Lisp黑客竹内郁雄审校本书前半介绍基本GC算法，包括标记-清除GC、引用计数、复制算法的GC、串行GC的算法、并发GC的算法等。后半介绍V8、Rubinius、Dalvik、CPython等几种具体GC的实现。本书适合各领域程序员阅读。", 4, "安徒生",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("代码之髓", "https://img9.doubanio.com/view/subject/l/public/s27445100.jpg", 8.1, 18.0, "《代码之髓：编程语言核心概念》作者从编程语言设计的角度出发，围绕语言中共通或特有的核心概念，通过语言演变过程中的纵向比较和在多门语言中的横向比较，清晰地呈现了程序设计语言中函数、类型、作用域、类、继承等核心知识。本书旨在帮助读者更好地理解各种概念是因何而起，并在此基础上更好地判断为何使用、何时使用及怎样使用。同时，在阅读本书后，读者对今后不断出现的新概念的理解能力也将得到提升。《代码之髓：编程语言", 10, "朱子杰",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("发布！设计与部署稳定的分布式系统（第2版）", "https://img9.doubanio.com/view/subject/l/public/s33570595.jpg", 7.9, 19.8, "作者根据自己的亲身经历和某些大型企业的案例，讲述了如何创建高稳定性的软件系统，分析了设计和实现中导致系统出现问题的原因。全书分为四个部分，每部分内容都由一个研究案例引出。第一部分介绍了如何保证系统的生存，即维护系统正常运行。第二部分介绍了为生产环境而设计，从基础层、实例层、互连层和控制层等方面构建系统安全性。第三部分讲述了交付系统，列出系统在部署过程中有可能出现的问题。第四部分引入适用性和混沌工程", 7, "张跃平",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("精通以太坊：开发智能合约和去中心化应用", "https://img9.doubanio.com/view/subject/l/public/s32302946.jpg", 8.4, 24.99, "为什么IBM、微软、纳斯达克以及其他数百个组织都在尝试以太坊？这本必不可少的指南向你展示了成为这个不断发展和令人兴奋的新兴行业的创新者所需的技能：·运行以太坊客户端，创建和传输交易，编写智能合约。·了解公钥加密、哈希的基本要素和数字签名。·了解“钱包”如何保存用来操纵以太币和智能合约的数字私钥。·使用JavaScript库和远程过程调用接口，以编程方式与以太坊客户端交互。·了解安全实践、设计模式和", 5, "陈独秀",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("别怕，Excel VBA其实很简单（第2版）", "https://img9.doubanio.com/view/subject/l/public/s28833761.jpg", 7.8, 12.0, "对于大部分没有编程基础的职场人士来说，在学习VBA时往往会有很大的畏难情绪。本书正是针对这样的人群，用浅显易懂的语言和生动形象的比喻，并配合大量插画，对Excel中看似复杂的概念和代码，从简单的宏录制、VBA编程环境和基础语法的介绍，到常用对象的操作与控制、执行程序的自动开关—对象的事件、设计自定义的操作界面、调试与优化编写的代码，都进行了形象的介绍。本书适合那些希望提高工作效率的职场人士，特别是", 9, "安徒生",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("挑战编程技能", "https://img9.doubanio.com/view/subject/l/public/s29347136.jpg", 5.0, 17.4, "新手程序员在具备了理论基础后，面对实际项目时，往往不知道如何解决问题；有经验的程序员在学习了一门新语言后，也会有很多不知道如何使用的特性。针对程序员的这一普遍困惑，资深软件工程师Brian P. Hogan在这本书中总结了57道练习题，教他们如何锤炼技能。这些练习题的难度会逐渐增加，使得编程训练充满挑战又乐趣多多。", 7, "石头",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("深入分析Java Web技术内幕（修订版）", "https://img9.doubanio.com/view/subject/l/public/s29237536.jpg", 7.5, 25.0, "《深入分析Java Web技术内幕（修订版）》新增了淘宝在无线端的应用实践，包括：CDN 动态加速、多终端化改造、 多终端Session 统一 ，以及在大流量的情况下，如何跨越性能、网络和一个地区的电力瓶颈等内容，并提供了比较完整的解决方案。《深入分析Java Web技术内幕（修订版）》主要围绕Java Web 相关技术从三方面全面、深入地进行了阐述。首先介绍前端知识，即在JavaWeb 开发中涉", 5, "周树人",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("疯狂Java", "https://img9.doubanio.com/view/subject/l/public/s8490944.jpg", 7.5, 24.99, "《疯狂Java：突破程序员基本功的16课(修订版)》是Java领域著名研究专家、Java语言培训导师、“疯狂Java”创始人李刚老师的又一倾心力作。在《疯狂Java：突破程序员基本功的16课(修订版)》中，李刚老师专门针对Java初学者可能会遇到的各种学习问题，由点及面，详细讨论了Java内存管理、Java编程过程中常遇陷阱、常用数据结构的Java实现和Java程序开发的方法与经验等内容。这些问题", 8, "周树人",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("机器学习实战（原书第2版）", "https://img9.doubanio.com/view/subject/l/public/s33732686.jpg", 9.7, 25.6, "这本机器学习畅销书基于TensorFlow 2和Scikit-Learn的新版本进行了全面更新，通过具体的示例、非常少的理论和可用于生产环境的Python框架，从零帮助你直观地理解并掌握构建智能系统所需要的概念和工具。全书分为两部分。第一部分介绍机器学习基础，涵盖以下主题：什么是机器学习，它试图解决什么问题，以及系统的主要类别和基本概念；第二部分介绍神经网络和深度学习，涵盖以下主题：什么是神经网络", 2, "周树人",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Linux命令行大全", "https://img9.doubanio.com/view/subject/l/public/s27318531.jpg", 8.1, 12.49, "《Linux命令行大全》主要介绍Linux命令行的使用，循序渐进，深入浅出，引导读者全面掌握命令行的使用方法。《Linux命令行大全》分为四部分。第一部分开始了对命令行基本语言的学习之旅，包括命令结构、文件系统的导引、命令行的编辑以及关于命令的帮助系统和使用手册。第二部分主要讲述配置文件的编辑，用于计算机操作的命令行控制。第三部分讲述了从命令行开始执行的常规任务。类UNIX操作系统，比如Linux", 9, "刘宛晴",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("性能之巅", "https://img9.doubanio.com/view/subject/l/public/s28283455.jpg", 8.6, 24.99, "《性能之巅：洞悉系统、企业与云计算》基于Linux 和Solaris 系统阐述了适用于所有系统的性能理论和方法，Brendan Gregg 将业界普遍承认的性能方法、工具和指标收集于本书之中。阅读本书，你能洞悉系统运作的方式，学习到分析和提高系统与应用程序性能的方法，这些性能方法同样适用于大型企业与云计算这类最为复杂的环境的性能分析与调优。", 5, "刘宛晴",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("机器学习实战", "https://img9.doubanio.com/view/subject/l/public/s26696371.jpg", 8.1, 31.99, "机器学习是人工智能研究领域中一个极其重要的研究方向，在现今的大数据时代背景下，捕获数据并从中萃取有价值的信息或模式，成为各行业求生存、谋发展的决定性手段，这使得这一过去为分析师和数学家所专属的研究领域越来越为人们所瞩目。本书第一部分主要介绍机器学习基础，以及如何利用算法进行分类，并逐步介绍了多种经典的监督学习算法，如k近邻算法、朴素贝叶斯算法、Logistic回归算法、支持向量机、AdaBoost", 8, "王伟",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("深入理解Kafka：核心设计与实践原理", "https://img9.doubanio.com/view/subject/l/public/s29982933.jpg", 9.6, 14.99, "《深入理解Kafka：核心设计与实践原理》从Kafka的基础概念切入，循序渐进地转入对其内部原理的剖析。《深入理解Kafka：核心设计与实践原理》主要阐述了Kafka中生产者客户端、消费者客户端、主题与分区、日志存储、原理解析、监控管理、应用扩展及流式计算等内容。虽然Kafka的内核使用Scala语言编写，但《深入理解Kafka：核心设计与实践原理》基本以Java语言作为主要的示例语言，方便大多数", 4, "安徒生",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("汇编语言(第3版)", "https://img9.doubanio.com/view/subject/l/public/s27987243.jpg", 9.3, 25.6, "《汇编语言(第3版)》具有如下特点：采用了全新的结构对课程的内容进行组织，对知识进行最小化分割，为读者构造了循序渐进的学习线索；在深入本质的层面上对汇编语言进行讲解；对关键环节进行深入的剖析。《汇编语言(第3版)》可用作大学计算机专业本科生的汇编教材及希望深入学习计算机科学的读者的自学教材。", 6, "成超",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Elasticsearch实战", "https://img9.doubanio.com/view/subject/l/public/s29924552.jpg", 7.7, 24.99, "本书主要展示如何使用Elasticsearch构建可扩展的搜索应用程序。书中覆盖了Elasticsearch的主要特性，从使用不同的分析器和查询类型进行相关性调优，到使用聚集功能进行实时性分析，还有地理空间搜索和文档过滤等更多吸引人的特性。全书共分两个部分，第一部分解释了核心特性，内容主要涉及Elasticsearch的介绍，数据的索引、更新和删除，数据的搜索，数据的分析，使用相关性进行搜索，使用", 3, "安徒生",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Python与机器学习实战", "https://img9.doubanio.com/view/subject/l/public/s29490111.jpg", 7.6, 40.79, "Python与机器学习这一话题是如此的宽广，仅靠一本书自然不可能涵盖到方方面面，甚至即使出一个系列的书也难能做到这点。单就机器学习而言，其领域就包括但不限于如下：有监督学习（Supervised Learning），无监督学习（Unsupervised Learning）和半监督学习（Semi-Supervised Learning）。而其具体的问题又大致可以分为两类：分类问题（Classific", 7, "鲁迅",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("C Primer Plus", "https://img9.doubanio.com/view/subject/l/public/s28683508.jpg", 9.8, 11.8, "《C Primer Plus（第6版）英文版》详细讲解了C语言的基本概念和编程技巧。《C Primer Plus（第6版）英文版》共17章。第1、2章介绍了C语言编程的预备知识。第3~15章详细讲解了C语言的相关知识，包括数据类型、格式化输入/输出、运算符、表达式、语句、循环、字符输入和输出、函数、数组和指针、字符和字符串函数、内存管理、文件输入输出、结构、位操作等。第16章、17章介绍C预处理器", 6, "刘宛晴",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("HTML5 Canvas核心技术", "https://img9.doubanio.com/view/subject/l/public/s26544945.jpg", 8.6, 19.99, "海报：", 7, "王伟",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("代码精进之路 从码农到工匠", "https://img9.doubanio.com/view/subject/l/public/s33554266.jpg", 7.4, 17.99, "这是一本为专业程序员而写的书，写好代码、追求卓越和工匠精神是每个程序员都应该具备的优秀品质。本书共有13章内容，主要分为技艺部分、思想部分和实践部分。技艺部分详细介绍了编程技巧和方法论，并配以详尽的代码案例，有助于读者提高编写代码的能力，优化代码质量。思想部分主要包括抽象能力、分治思想，以及程序员应该具备的素养等内容。实践部分主要介绍了常见的应用架构模式，以及COLA架构的设计原理。", 2, "鲁迅",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("深入理解并行编程", "https://img9.doubanio.com/view/subject/l/public/s29484427.jpg", 7.0, 15.8, "《深入理解并行编程》首先以霍金提出的两个理论物理限制为引子，解释了多核并行计算兴起的原因，并从硬件的角度阐述并行编程的难题。接着，《深入理解并行编程》以常见的计数器为例，探讨其不同的实现方法及适用场景。在这些实现方法中，除了介绍常见的锁以外，《深入理解并行编程》还重点介绍了RCU的使用及其原理，以及实现RCU的基础：内存屏障。最后，《深入理解并行编程》还介绍了并行软件的验证，以及并行实时计算等内容", 3, "周树人",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("移动App性能评测与优化", "https://img9.doubanio.com/view/subject/l/public/s29093099.jpg", 8.2, 14.75, "在移动应用开发中，应用上线了只是一个开始，噩梦在后面：手机越用越卡为哪般？手机发烫是为何？应用的流畅度怎样才能丝般的润滑？坑爹的路线图怎么预防？谁偷走的用户的钱包？如何瘦成一道闪电？这些问题解决起来都是非常麻烦的，腾讯移动品质中心（TMQ）成立了专项测试团队来解决这些问题，这些程师们长期负责腾讯公司部分重要的手机应用的性能评测与优化工作，在App的内存、电量、流量、流畅度、网络、安装包大小等核心性", 5, "成超",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Ruby元编程（第2版）", "https://img9.doubanio.com/view/subject/l/public/s28259406.jpg", 9.2, 13.8, "《Ruby元编程（第2版）》在大量剖析实例代码的基础上循序渐进地介绍Ruby特有的实用编程技巧。通过分析案例、讲解例题、回顾Ruby类库的实现细节，作者不仅向读者展示了元编程的优势及其解决问题的方式，更详细列出33种发挥其优势的编程技巧。本书堪称动态语言设计模式。Ruby之父松本行弘作序推荐。", 10, "储成龙",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("深入浅出MySQL", "https://img9.doubanio.com/view/subject/l/public/s27208567.jpg", 7.6, 18.0, "《深入浅出MySQL：数据库开发、优化与管理维护(第2版)》从数据库的基础、开发、优化、管理维护和架构5个方面对MySQL进行了详细的介绍，每一部分都独立成篇。基础篇主要适合于MySQL的初学者阅读，包括MySQL的安装与配置、SQL基础、MySQL支持的数据类型、MySQL中的运算符、常用函数、图形化工具的使用等内容。开发篇主要适合于MySQL的设计和开发人员阅读，内容包括表类型(存储引擎)的选", 4, "金浩然",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("图解HTTP", "https://img9.doubanio.com/view/subject/l/public/s27283822.jpg", 8.1, 9.6, "本书对互联网基盘——HTTP协议进行了全面系统的介绍。作者由HTTP协议的发展历史娓娓道来，严谨细致地剖析了HTTP协议的结构，列举诸多常见通信场景及实战案例，最后延伸到Web安全、最新技术动向等方面。本书的特色为在讲解的同时，辅以大量生动形象的通信图例，更好地帮助读者深刻理解HTTP通信过程中客户端与服务器之间的交互情况。读者可通过本书快速了解并掌握HTTP协议的基础，前端工程师分析抓包数据，后", 9, "陈独秀",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Go语言高级编程", "https://img9.doubanio.com/view/subject/l/public/s33305844.jpg", 8.1, 14.99, "本书从实践出发讲解Go语言编程的进阶知识。本书共6章，第1章简单回顾Go语言的发展历史；第2章和第3章系统介绍CGO编程和Go汇编语言的用法；第4章对RPC和Protobuf技术进行深入介绍，并讲述如何打造一个自己的RPC系统；第5章介绍工业级环境的Web系统的设计和相关技术；第6章介绍Go语言在分布式领域的一些编程技术。", 10, "成超",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Effective JavaScript", "https://img9.doubanio.com/view/subject/l/public/s27553548.jpg", 8.4, 9.8, "Effective 系列丛书经典著作，亚马逊五星级畅销书，Ecma 的JavaScript 标准化委员会著名专家撰写，JavaScript 语言之父、Mozilla CTO —— Brendan Eich 作序鼎力推荐！作者凭借多年标准化委员会工作和实践经验，深刻辨析JavaScript 的内部运作机制、特性、陷阱和编程最佳实践，将它们高度浓缩为极具实践指导意义的 68 条精华建议。本书共分为 7", 6, "金浩然",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("深度学习推荐系统", "https://img9.doubanio.com/view/subject/l/public/s33607323.jpg", 9.4, 40.05, "深度学习在推荐系统领域掀起了一场技术革命，本书从深度学习推荐模型、Embedding技术、推荐系统工程实现、模型评估体系、业界前沿实践等几个方面介绍了这场技术革命中的主流技术要点。《深度学习推荐系统》既适合推荐系统、计算广告和搜索领域的从业者阅读，也适合人工智能相关专业的本 科生、研究生、博士生阅读，帮助他们建立深度学习推荐系统的技术框架，通过学习前沿案例 加强深度学习理论与推荐系统工程实践的融合", 1, "金浩然",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("程序是怎样跑起来的", "https://img9.doubanio.com/view/subject/l/public/s28047380.jpg", 8.0, 12.49, "本书从计算机的内部结构开始讲起，以图配文的形式详细讲解了二进制、内存、数据压缩、源文件和可执行文件、操作系统和应用程序的关系、汇编语言、硬件控制方法等内容，目的是让读者了解从用户双击程序图标到程序开始运行之间到底发生了什么。同时专设了“如果是你，你会怎样介绍？”专栏，以小学生、老奶奶为对象讲解程序的运行原理，颇为有趣。本书图文并茂，通俗易懂，非常适合计算机爱好者及相关从业人员阅读。", 8, "猪猪",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("编程语言实现模式", "https://img9.doubanio.com/view/subject/l/public/s7661036.jpg", 8.4, 24.99, "《编程语言实现模式》旨在传授开发语言应用（工具）的经验和理念，帮助读者构建自己的语言应用。这里的语言应用并非特指用编译器或解释器实现编程语言，而是泛指任何处理、分析、翻译输入文件的程序，比如配置文件读取器、数据读取器、模型驱动的代码生成器、源码到源码的翻译器、源码分析工具、解释器，以及诸如此类的工具。为此，作者举例讲解已有语言应用的工作机制，拆解、归纳出31种易于理解且常用的设计模式（每种都包括通", 7, "马丽芳",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("漫画算法：小灰的算法之旅", "https://img9.doubanio.com/view/subject/l/public/s32299271.jpg", 8.5, 12.49, "《漫画算法：小灰的算法之旅》通过虚拟的主人公小灰的心路历程，用漫画的形式讲述了算法和数据结构的基础知识、复杂多变的算法面试题目及算法的实际应用场景。第1章 介绍了算法和数据结构的相关概念，告诉大家算法是什么，数据结构又是什么，它们有哪些用途，如何分析时间复杂度，如何分析空间复杂度。第2章 介绍了最基本的数据结构，包括数组、链表、栈、队列、哈希表的概念和读写操作。第3章 介绍了树和二叉树的概念、二叉", 4, "刘汝佳",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Linux/UNIX系统编程手册", "https://img9.doubanio.com/view/subject/l/public/s27199116.jpg", 9.4, 25.6, "《linux/unix系统编程手册(上、下册)》是介绍linux与unix编程接口的权威著作。linux编程资深专家michael kerrisk在书中详细描述了linux/unix系统编程所涉及的系统调用和库函数，并辅之以全面而清晰的代码示例。《linux/unix系统编程手册(上、下册)》涵盖了逾500个系统调用及库函数，并给出逾200个程序示例，另含88张表格和115幅示意图。《linux/", 10, "猪猪",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("深入理解Java虚拟机（第3版）", "https://img9.doubanio.com/view/subject/l/public/s33531736.jpg", 9.5, 14.99, "内容介绍这是一部从工作原理和工程实践两个维度深入剖析JVM的著作，是计算机领域公认的经典，繁体版在台湾也颇受欢迎。自2011年上市以来，前两个版本累计印刷36次，销量超过30万册，两家主要网络书店的评论近90000条，内容上近乎零差评，是原创计算机图书领域不可逾越的丰碑。第3版在第2版的基础上做了重大修订，内容更丰富、实战性更强：根据新版JDK对内容进行了全方位的修订和升级，围绕新技术和生产实践新", 7, "猪猪",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("高效程序员的45个习惯（修订版）", "https://img9.doubanio.com/view/subject/l/public/s27586443.jpg", 7.8, 19.8, "本书总结并生动地阐述了成为高效的开发人员所需具备的45 个习惯、思想观念和方法，涵盖了软件开发进程、编程和调试工作、开发者态度、项目和团队管理以及持续学习等几方面。本书适合所有程序员阅读。", 8, "曹文轩",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("迷茫的旅行商", "https://img9.doubanio.com/view/subject/l/public/s27028577.jpg", 8.2, 1.99, "假设一名旅行商打算拜访一张城市列表中的所有城市，每座城市只去一次，最后回到出发地。要怎么走才能让路线最短呢？这就是旅行商问题，乍一听很简单，在应用数学界却是一道研究极其热烈的难题，时至今日仍无人能解。本书中，William J. Cook将带领读者踏上一场数学之旅，跟随旅行商的脚步，从19世纪初爱尔兰数学家W. R. Hamilton最初定义该问题开始，一路奔向当今最前沿、最顶尖的解题尝试。作者追", 10, "储成龙",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Java实战（第2版）", "https://img9.doubanio.com/view/subject/l/public/s33524970.jpg", 8.8, 29.8, "现代Java应用充分利用了微服务、反应式架构以及流式数据等创新设计。现代Java特性，譬如Lambda、流以及大家期待已久的Java模块系统让这些设计的实现极其便利。是时候更新技能工具箱了，只有这样，你才能从容应对迎面而来的种种挑战！本书通过透彻的示例和通俗的语言讲解了Java语言这些最激动人心的特性，作者注重细节，努力降低了学习难度，节省你宝贵的时间。依照本书边学边练，你可以很快掌握流应用程序接", 10, "莫言",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("移动开发架构设计实战", "https://img9.doubanio.com/view/subject/l/public/s33509199.jpg", 8.5, 25.0, "《移动开发架构设计实战》覆盖了移动开发中涉及的多种架构模式，基于Android平台对架构模式进行实战，可以帮助入门开发者了解架构模式，进阶开发者掌握架构模式，技术领导者进行架构选型。《移动开发架构设计实战》内容包括：流行架构模型MVX系列——MVC架构、MVP架构、MVVM架构，依赖注入框架Dagger2，函数响应式框架RxJava2，Google推荐的生命周期感知架构组件AAC，整洁的架构“Th", 9, "刘汝佳",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("自己动手写CPU", "https://img9.doubanio.com/view/subject/l/public/s27408841.jpg", 8.5, 23.99, "《自己动手写CPU(含CD光盘1张)》使用Verilog HDL 设计实现了一款兼容MIPS32指令集架构的处理器——OpenMIPS。OpenMIPS 处理器具有两个版本，分别是教学版和实践版。教学版的主要设计思想是尽量简单，处理器的运行情况比较理想化，与教科书相似，便于使用其进行教学、学术研究和讨论，也有助于学生理解课堂上讲授的知识。实践版的设计目标是能完成特定功能，发挥实际作用。《自己动手写", 4, "张跃平",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("算法心得：高效算法的奥秘（原书第2版）", "https://img9.doubanio.com/view/subject/l/public/s27240262.jpg", 8.8, 17.99, "【编辑推荐】由在IBM工作50余年的资深计算机专家撰写，Amazon全五星评价，算法领域最有影响力的著作之一Google公司首席架构师、Jolt大奖得主Hoshua Bloch和Emacs合作创始人、C语言畅销书作者Guy Steele倾情推荐算法的艺术和数学的智慧在本书中得到了完美体现，书中总结了大量高效、优雅和奇妙的算法，并从数学角度剖析了其背后的原理【读者评价】“这是第一本宣称能讲解计算机算", 3, "王伟",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("深度探索Linux操作系统", "https://img9.doubanio.com/view/subject/l/public/s27318529.jpg", 8.4, 29.8, "《深度探索linux操作系统：系统构建和原理解析》是探索linux操作系统原理的里程碑之作，在众多的同类书中独树一帜。它颠覆和摒弃了传统的从阅读linux内核源代码着手学习linux操作系统原理的方式，而是基于实践，以从零开始构建一个完整的linux操作系统的过程为依托，指引读者在实践中去探索操作系统的本质。这种方式的妙处在于，让读者先从宏观上全面认清一个完整的操作系统中都包含哪些组件，各个组件的", 9, "石头",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("程序员修炼之道(第2版)", "https://img9.doubanio.com/view/subject/l/public/s33607450.jpg", 9.1, 22.99, "《程序员修炼之道》之所以在全球范围内广泛传播，被一代代开发者奉为圭臬，盖因它可以创造出真正的价值：或编写出更好的软件，或探究出编程的本质，而所有收获均不依赖于特定语言、框架和方法。时隔20年的新版，经过全面的重新选材、组织和编写，覆盖哲学、方法、工具、设计、解耦、并发、重构、需求、团队等务实话题的最佳实践及重大陷阱，以及易于改造、复用的架构技术。本书极具洞察力与趣味性，适合从初学者到架构师的各阶层", 10, "刘汝佳",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("简约之美", "https://img9.doubanio.com/view/subject/l/public/s24642460.jpg", 7.2, 18.0, "《简约之美:软件设计之道》将软件设计作为一门严谨的科学，阐述了开发出优雅简洁的代码所应该遵循的基本原则。作者从为什么以前软件设计没有像数学等学科一样成为一门科学开始入手，道出了软件以及优秀的软件设计的终极目标，并给出了具体的指导规则。", 10, "李大钊",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("JavaScript模式", "https://img9.doubanio.com/view/subject/l/public/s11337059.jpg", 8.7, 9.8, "《JavaScript模式》包含了实现每个讨论的模式的实践建议，并附有数个可以立即上手的范例。同时还可以学到一些反模式，一些常见的编程方式，这些编程方式引发的问题比解决的还多。它不是一本入门级的书，而是适用于希望将自身的Javascript技巧提高到一个新层次的专业的开发人员和程序员。全书共八章节，内容包括简介、基本技巧、字面量和构造函数、函数、对象创建模式、代码复用模式等。", 2, "李大钊",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("计算机组成与设计（原书第5版）", "https://img9.doubanio.com/view/subject/l/public/s28305877.jpg", 9.5, 79.2, "《计算机组成与设计：硬件/软件接口》是计算机组成与设计的经典畅销教材，第5版经过全面更新，关注后PC时代发生在计算机体系结构领域的革命性变革——从单核处理器到多核微处理器，从串行到并行。本书特别关注移动计算和云计算，通过平板电脑、云体系结构以及ARM（移动计算设备）和x86（云计算）体系结构来探索和揭示这场技术变革。与前几版一样，本书采用MIPS处理器讲解计算机硬件技术、汇编语言、计算机算术、流水", 4, "朱子杰",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("实战Java高并发程序设计", "https://img9.doubanio.com/view/subject/l/public/s29405037.jpg", 8.2, 19.8, "在过去单核CPU时代，单任务在一个时间点只能执行单一程序，随着多核CPU的发展，并行程序开发就显得尤为重要。《实战Java高并发程序设计》主要介绍基于Java的并行程序设计基础、思路、方法和实战。第一，立足于并发程序基础，详细介绍Java中进行并行程序设计的基本方法。第二，进一步详细介绍JDK中对并行程序的强大支持，帮助读者快速、稳健地进行并行程序开发。第三，详细讨论有关“锁”的优化和提高并行程序", 7, "周树人",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Python金融大数据分析 第2版", "https://img9.doubanio.com/view/subject/l/public/s33609011.jpg", 8.5, 13.99, "Python已成为数据驱动AI、金融优先选择的编程语言。现在，一些大型的投资银行和对冲资金均使用Python及其生态系统来构建核心交易与风险管理系统。在本书中，作者向开发人员和量化分析人员介绍了使用Python程序库与工具，完成金融数据科学、算法交易和计算金融任务的方法。Python与金融：Python交互式金融分析与程序开发入门。基本知识：学习Python数据类型与结构、NumPy、pandas", 5, "曹文轩",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("数据库索引设计与优化", "https://img9.doubanio.com/view/subject/l/public/s28104652.jpg", 8.6, 24.99, "《数据库索引设计与优化》提供了一种简单、高效、通用的关系型数据库索引设计方法。作者通过系统的讲解及大量的案例清晰地阐释了关系型数据库的访问路径选择原理，以及表和索引的扫描方式，详尽地讲解了如何快速地估算SQL 运行的CPU 时间及执行时间，帮助读者从原理上理解SQL、表及索引结构、访问方式等对关系型数据库造成的影响，并能够运用量化的方法进行判断和优化，指导关系型数据库的索引设计。《数据库索引设计与", 1, "成超",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("C语言入门经典（第5版）", "https://img9.doubanio.com/view/subject/l/public/s27192924.jpg", 9.3, 40.05, "C语言是每一位程序员都应该掌握的基础语言。C语言是微软。NET编程中使用的C#语言的基础；C语言是iPhone、iPad和其他苹果设备编程中使用的Objective-C语言的基础；C语言是在很多环境中（包括GNU项目）被广泛使用的C++语言的基础。C语言也是Linux操作系统及其很多功能的基础。学习C语言可以给编程职业生涯提供牢固的基础，也有助于更好地理解更为现代的语言（如Java）。《C语言入门", 8, "储成龙",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("父与子的编程之旅", "https://img9.doubanio.com/view/subject/l/public/s27490779.jpg", 8.5, 12.0, "本书是一本家长与孩子共同学习编程的入门书。作者是一对父子，他们以Python语言为例，详尽细致地介绍了Python如何安装、字符串和操作符等程序设计的基本概念，介绍了条件语句、函数、模块等进阶内容，最后讲解了用Python实现游戏编程。书中的语言生动活泼，叙述简单明了。 为了让学习者觉得编程有趣，本书编排了很多卡通人物及场景对话，让学习者在轻松愉快之中跨入计算机编程的大门。第 2 版新增内容首先，", 4, "刘汝佳",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("R语言入门与实践", "https://img9.doubanio.com/view/subject/l/public/s28776989.jpg", 9.2, 29.99, "本书精心策划了三个虚拟项目，将数据科学家必需的专业技能融合其中，教会读者如何将数据存储到计算机内存中，如何在必要的时候转换内存中的数据值，如何用R编写自己的程序并将其用于数据分析和模拟运行。读者将跟随世界一流的RStudio培训师掌握宝贵的编程技能，并借助这些技能成为优秀的数据科学家。", 4, "莫言",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("深入React技术栈", "https://img9.doubanio.com/view/subject/l/public/s29162154.jpg", 8.1, 9.8, "全面讲述React技术栈的第一本原创图书，pure render专栏主创倾力打造覆盖React、Flux、Redux及可视化，帮助开发者在实践中深入理解技术和源码前端组件化主流解决方案，一本书玩转React“全家桶”本书讲解了非常多的内容，不仅介绍了面向普通用户的API、应用架构和周边工具，还深入介绍了底层实现。此外，本书非常重视实战，每一节都有实际的例子，细节丰富。我从这本书里学到了很多东西，强", 7, "金浩然",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Python网络编程（第3版）", "https://img9.doubanio.com/view/subject/l/public/s29028877.jpg", 8.9, 29.99, "本书针对想要深入理解使用Python来解决网络相关问题或是构建网络应用程序的技术人员，结合实例讲解了网络协议、网络数据及错误、电子邮件、服务器架构和HTTP及Web应用程序等经典话题。具体内容包括：全面介绍Python3中最新提供的SSL支持，异步I/O循环的编写，用Flask框架在Python代码中配置URL，跨站脚本以及跨站请求伪造攻击网站的原理及保护方法，等等。", 8, "马丽芳",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Processing语言权威指南", "https://img9.doubanio.com/view/subject/l/public/s27080847.jpg", 9.5, 34.0, "本书介绍了可视化艺术中的计算机编程概念，对开源编程语言Processing作了非常详尽的阐述。学生、艺术家、设计师、建筑师、研究者，以及任何想编程实现绘画、动画和互动的人都可以使用它。书中的大部分章节是短小的单元，介绍Processing的语法和基本概念（变量、函数、面向对象编程），涵盖与软件相关的图像处理、绘制，并且给出了大量简短的原型程序，配以相应的过程图像与注释。书中还有一些访谈文章，与动画", 7, "魔方",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Elixir 程序设计", "https://img9.doubanio.com/view/subject/l/public/s28617006.jpg", 7.7, 24.99, "在了解 Elixir 前，在网上看到一些评论说， Elixir 其实就是用 Ruby 的语法来编写 Erlang 程序。其实不然。真正玩了 Elixir 之后，就会发现这纯属幻觉。因为 Elixir 相对于 Ruby 使用了截然不同的语言范式——函数式编程。两者在编程思想上相距甚远。如果说它看似 Ruby ，也仅仅是 Elixir 设计者 José Valim ，一个 Ruby 开发者的情怀使然罢", 4, "鲁迅",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("创新者", "https://img9.doubanio.com/view/subject/l/public/s29414569.jpg", 8.5, 14.46, "《创新者》是沃尔特·艾萨克森继全球畅销书《史蒂夫·乔布斯传》之后的又一部力作，不仅讲述了计算机和互联网从无到有的发展历程，为我们 生动地刻画出数字时代的创新者群像，还深度挖掘互联网的精神内核，解读了“诗意科学”这个重大主题。在近200年的数字化进程中群星闪耀，艾萨克森从第一个计算机程序的创造者、浪漫主义诗人拜伦之女埃达•洛夫莱斯伯爵夫人说起，细数了这一群将科学与人文融合的创新者，他们包括第一台通用", 2, "鲁迅",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Redis设计与实现", "https://img9.doubanio.com/view/subject/l/public/s27297117.jpg", 8.6, 24.99, "【官方网站】本书的官方网站 www.RedisBook.com 提供了书本试读、相关源码下载和勘误回报等服务，欢迎读者浏览和使用。【编辑推荐】系统而全面地描述了 Redis 内部运行机制图示丰富，描述清晰，并给出大量参考信息，是NoSQL数据库开发人员案头必备包括大部分Redis单机特征，以及所有多机特性【读者评价】这本书描述的知识点很丰富，覆盖很全，里面提到特性较多，有不少我们也没用过 :) 每", 3, "储成龙",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("重构", "https://img9.doubanio.com/view/subject/l/public/s28259431.jpg", 8.9, 40.05, "本书清晰揭示了重构的过程，解释了重构的原理和最佳实践方式，并给出了何时以及何地应该开始挖掘代码以求改善。书中给出了70 多个可行的重构，每个重构都介绍了一种经过验证的代码变换手法的动机和技术。本书提出的重构准则将帮助你一次一小步地修改你的代码，从而减少了开发过程中的风险。", 5, "朱子杰",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Python编程快速上手", "https://img9.doubanio.com/view/subject/l/public/s28895767.jpg", 8.9, 12.0, "如今，人们面临的大多数任务都可以通过编写计算机软件来完成。Python是一种解释型、面向对象、动态数据类型的高级程序设计语言。通过Python编程，我们能够解决现实生活中的很多任务。本书是一本面向实践的Python编程实用指南。本书的目的，不仅是介绍Python语言的基础知识，而且还通过项目实践教会读者如何应用这些知识和技能。本书的首部分介绍了基本Python编程概念，第二部分介绍了一些不同的任务", 9, "周树人",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Java虚拟机并发编程", "https://img9.doubanio.com/view/subject/l/public/s27208309.jpg", 7.1, 24.99, "苏布拉马尼亚姆所著的《Java虚拟机并发编程》是Java并发编程领域的里程碑之作，由资深Java技术专家、并发编程专家、敏捷开发专家和Jolt大奖得主撰写，Amazon五星级畅销书。它系统深入地讲解在JVM平台上如何利用JDK同步模型、软件事务内存模型和基于角色的并发模型更好地进行并发编程。全书以示例驱动，通俗易懂，包含大量编程技巧、注意事项和最佳实践。要重点强调的是，本书并不仅仅只适合于Java", 5, "李大钊",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("改变未来的九大算法", "https://img9.doubanio.com/view/subject/l/public/s27271263.jpg", 7.7, 9.6, "Google得出的搜索结果是如何产生的？百度为何会陷入“搜索门”，又是什么机制使然？身处在大数据时代的我们，究竟该如何应对变化莫测的世界？……没有满篇的专业术语，第一次让我们通过简单明了的语言、生动的例证了解支撑计算机王国的灵魂支柱——9大算法，包括人工智能、数据压缩，以及Google著名的PageRank等。本书精彩地介绍了搜索引擎、PageRank、公开密钥加密、纠错码、模式识别、数据压缩、数", 8, "猪猪",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("JavaScript权威指南(第6版)", "https://img9.doubanio.com/view/subject/l/public/s9056065.jpg", 8.8, 9.8, "本书是程序员学习核心JavaScript语言和由Web浏览器定义的JavaScript API的指南和综合参考手册。第6版涵盖HTML 5和ECMAScript 5。很多章节完全重写，以便与时俱进，紧跟当今的最佳Web开发实践。本书新增章节描述了jQuery和服务器端JavaScript。本书适合那些希望学习Web编程语言的初、中级程序员和希望精通JavaScript的JavaScript程序员阅", 1, "储成龙",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Git版本控制管理（第2版）", "https://img9.doubanio.com/view/subject/l/public/s28024067.jpg", 7.3, 16.99, "Git是一款免费、开源的分布式版本控制系统，最早由LinilusTorvalds创建，用于管理Linux内核开发，现已成为分布式版本控制的主流工具。《Git版本控制管理（第2版）》是学习掌握Git的最佳教程，总共分为21章，其内容涵盖了如何在多种真实开发环境中使用Git；洞察Git的常用案例、初始任务和基本功能；如何在集中和分布式版本控制中使用Git；使用Git管理合并、冲突、补丁和差异；获得诸如", 4, "石头",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Python深度学习", "https://img9.doubanio.com/view/subject/l/public/s29839337.jpg", 9.6, 12.49, "本书由Keras之父、现任Google人工智能研究员的弗朗索瓦•肖莱（François Chollet）执笔，详尽介绍了用Python和Keras进行深度学习的探索实践，涉及计算机视觉、自然语言处理、生成式模型等应用。书中包含30多个代码示例，步骤讲解详细透彻。由于本书立足于人工智能的可达性和大众化，读者无须具备机器学习相关背景知识即可展开阅读。在学习完本书后，读者将具备搭建自己的深度学习环境、建", 6, "曹文轩",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Java核心技术·卷1：基础知识（原书第9版）", "https://img9.doubanio.com/view/subject/l/public/s27123141.jpg", 8.3, 25.0, "Java领域最有影响力和价值的著作之一，拥有20多年教学与研究经验的资深Java技术专家撰写（获Jolt大奖），与《Java编程思想》齐名，10余年全球畅销不衰，广受好评。第9版根据JavaSE7全面更新，同时修正了第8版中的不足，系统全面讲解Java语言的核心概念、语法、重要特性和开发方法，包含大量案例，实践性强。《Java核心技术·卷1：基础知识》共14章。第1章概述了Java语言与其他程序设", 5, "陈独秀",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Go程序设计语言", "https://img9.doubanio.com/view/subject/l/public/s29458403.jpg", 8.8, 27.99, "本书由《C程序设计语言》的作者Kernighan和谷歌公司Go团队主管Alan Donovan联袂撰写，是学习Go语言程序设计的指南。本书共13章，主要内容包括：Go的基础知识、基本结构、基本数据类型、复合数据类型、函数、方法、接口、goroutine、通道、共享变量的并发性、包、go工具、测试、反射等。本书适合作为计算机相关专业的教材，也可供Go语言爱好者阅读。", 9, "张跃平",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Effective Java中文版（第3版）", "https://img9.doubanio.com/view/subject/l/public/s32282160.jpg", 9.5, 14.99, "本书一共包含90个条目，每个条目讨论Java程序设计中的一条规则。这些规则反映了最有经验的优秀程序员在实践中常用的一些有益的做法。全书以一种比较松散的方式将这些条目组织成11章，每一章都涉及软件设计的一个主要方面。因此，本书并不一定需要按部就班地从头读到尾，因为每个条目都有一定程度的独立性。这些条目相互之间经常交叉引用，因此可以很容易地在书中找到自己需要的内容。本书的目标是帮助读者更加有效地使用J", 4, "储成龙",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Java并发编程实战", "https://img9.doubanio.com/view/subject/l/public/s7663093.jpg", 9.0, 19.8, "本书深入浅出地介绍了Java线程和并发，是一本完美的Java并发参考手册。书中从并发性和线程安全性的基本概念出发，介绍了如何使用类库提供的基本并发构建块，用于避免并发危险、构造线程安全的类及验证线程安全的规则，如何将小的线程安全类组合成更大的线程安全类，如何利用线程来提高并发应用程序的吞吐量，如何识别可并行执行的任务，如何提高单线程子系统的响应性，如何确保并发程序执行预期任务，如何提高并发代码的性", 10, "曹文轩",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("图解TCP/IP（第5版）", "https://img9.doubanio.com/view/subject/l/public/s26676928.jpg", 7.8, 24.99, "这是一本图文并茂的网络管理技术书籍，旨在让广大读者理解TCP/IP的基本知识、掌握TCP/IP的基本技能。书中讲解了网络基础知识、TCP/IP基础知识、数据链路、IP协议、IP协议相关技术、TCP与UDP、路由协议、应用协议、网络安全等内容，引导读者了解和掌握TCP/IP，营造一个安全的、使用放心的网络环境。本书适合计算机网络的开发、管理人员阅读，也可作为大专院校相关专业的教学参考书。", 10, "陈独秀",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Java编程的逻辑", "https://img9.doubanio.com/view/subject/l/public/s29680277.jpg", 9.1, 14.99, "Java专家撰写，力求透彻讲解每个知识点，逐步建立编程知识图谱。本书以Java语言为例，由基础概念入手，到背后实现原理与逻辑，再到应用实践，融会贯通。全书共六大部分，其要点如下。第一部分（第1～2章）讲解计算机程序的基本执行流程与元素，以及数据背后的二进制表示，帮读者掌握编程的基本概念。第二部分（第3～7章）讲解面向对象的编程原理与逻辑，涉及类、继承与多态、接口与抽象类、异常与常用基础类，让读者透", 9, "陈独秀",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Linux多线程服务端编程", "https://img9.doubanio.com/view/subject/l/public/s24522799.jpg", 8.8, 24.99, "本书主要讲述采用现代C++ 在x86-64 Linux 上编写多线程TCP 网络服务程序的主流常规技术，重点讲解一种适应性较强的多线程服务器的编程模型，即one loop per thread。这是在Linux 下以native 语言编写用户态高性能网络程序最成熟的模式，掌握之后可顺利地开发各类常见的服务端网络应用程序。本书以muduo 网络库为例，讲解这种编程模型的使用方法及注意事项。本书的宗旨", 2, "陈独秀",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("深度学习", "https://img9.doubanio.com/view/subject/l/public/s29518349.jpg", 8.4, 25.6, "《深度学习》由全球知名的三位专家Ian Goodfellow、Yoshua Bengio 和Aaron Courville撰写，是深度学习领域奠基性的经典教材。全书的内容包括3个部分：第1部分介绍基本的数学工具和机器学习的概念，它们是深度学习的预备知识；第2部分系统深入地讲解现今已成熟的深度学习方法和技术；第3部分讨论某些具有前瞻性的方向和想法，它们被公认为是深度学习未来的研究重点。《深度学习》适", 2, "安徒生",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("架构师应该知道的37件事", "https://img9.doubanio.com/view/subject/l/public/s33640987.jpg", 8.6, 29.8, "本书汇集了一名架构师20多年来在全球各大企业任职的经验，共分为5个部分，分别对应在帮助大型企业进行IT转型的过程中，首席架构师必须高效处理的5个方面：企业或IT架构师的角色和能力、架构工作在大型企业中的价值、与各种干系人的沟通、对组织结构和系统的理解、对传统组织进行转型。本书科学而系统地归纳出软件架构师应该具备的完整能力模型，不仅帮助软件开发人员系统地学习如何掌握这37项技能，而且还能让他们进一步", 2, "刘宛晴",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("黑客秘笈", "https://img9.doubanio.com/view/subject/l/public/s28270390.jpg", 6.6, 24.99, "所谓的渗透测试，就是借助各种漏洞扫描工具，通过模拟黑客的攻击方法，来对网络安全进行评估。本书采用大量真实案例和集邮帮助的建议讲解了在渗透测试期间会面临的一些障碍，以及相应的解决方法。本书共分为10章，其内容涵盖了本书所涉的攻击机器/工具的安装配置，网络扫描，漏洞利用，人工地查找和搜索Web应用程序的漏洞，攻陷系统后如何获取更重要的信息，社工方面的技巧，物理访问攻击，规避杀毒软件的方法，破解密码相关", 3, "李大钊",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("深入理解C++11", "https://img9.doubanio.com/view/subject/l/public/s26689304.jpg", 7.7, 12.0, "《深入理解C++11:C++11新特性解析与应用》内容简介：国内首本全面深入解读C++11新标准的专著，由C++标准委员会代表和IBM XL编译器中国开发团队共同撰写。不仅详细阐述了C++11标准的设计原则，而且系统地讲解了C++11新标准中的所有新语言特性、新标准库特性、对原有特性的改进，以及如何应用所有这些新特性。《深入理解C++11:C++11新特性解析与应用》一共8章：第1章从设计思维和应", 7, "猪猪",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Qt 5.9 C++开发指南", "https://img9.doubanio.com/view/subject/l/public/s29863993.jpg", 8.3, 13.0, "本书以Qt 5.9 LTS版本为开发平台，详细介绍了Qt C++开发应用程序的技术，包括Qt应用程序的基本架构、信号与槽工作机制、图形显示的Graphics/View架构、数据编辑和显示的Model/View架构、对话框和多窗口的设计与调用方法等，介绍了常用界面组件、文件读写、绘图、图表、数据可视化、数据库、多线程、网络和多媒体等模块的使用。每个编程主题都精心设计了完整的实例程序。通过阅读本书，可", 5, "金浩然",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Apache Kafka源码剖析", "https://img9.doubanio.com/view/subject/l/public/s29444479.jpg", 8.3, 29.8, "《Apache Kafka源码剖析》以Kafka 0.10.0版本源码为基础，针对Kafka的架构设计到实现细节进行详细阐述。《Apache Kafka源码剖析》共5章，从Kafka的应用场景、源码环境搭建开始逐步深入，不仅介绍Kafka的核心概念，而且对Kafka生产者、消费者、服务端的源码进行深入的剖析，最后介绍Kafka常用的管理脚本实现，让读者不仅从宏观设计上了解Kafka，而且能够深入到", 1, "金浩然",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("CPU自制入门", "https://img9.doubanio.com/view/subject/l/public/s27160217.jpg", 7.8, 29.99, "一直以来CPU内部是绝大多数IT工程师难以触及的领域。纵使学习过计算机架构相关课程，自己动手实现CPU也始终遥不可及，因为这涉及计算机系统的最底层——芯片设计。而近年来FPGA芯片产品的发展与普及打破了这一阻碍，利用内部电路可重编程的FPGA，我们几乎可以实现任何逻辑电路，自然也包括CPU。本书就是在这样一个背景下孕育而生的。本书利用FPGA，为读者开启了一个崭新的自制CPU的世界。全书分为3章，", 10, "安徒生",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("计算机：一部历史", "https://img9.doubanio.com/view/subject/l/public/s27191793.jpg", 7.7, 18.0, "◎内容简介：《计算机：一部历史》，给大众读者写的计算机科普读物，零门槛入门计算机科学。讲述计算机背后鲜为人知的故事，普及关于计算机和互联网，你不得不了解的知识。在过去数十年里，除非你一直与世隔绝，否则就不可能不受到信息革命的影响。我们身处技术演进史上的计算机时代，无论你是计算机和互联网的拥护者、反对者还是旁观者，无论你是否具备计算机专业背景，只要你使用计算机，这本书就是你的案头必备。一方面，本书介", 4, "张跃平",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("编程真好玩", "https://img9.doubanio.com/view/subject/l/public/s29581560.jpg", 8.8, 12.49, "在美国，编程已进入幼儿园和中小学课堂，是备受欢迎的课程之一。在英国，编程被列入国家教学大纲，成为6~15岁孩子的必修课。在芬兰，编程理念融入了小学的各门课程，孩子们可以随时随地学编程。编程已经成为世界的通用语言，和听、说、读、写、算一样，是孩子必须掌握的技能。Scratch是美国麻省理工学院设计开发的可视化少儿编程工具，全球1500多万孩子正在学习使用。它把枯燥乏味的数字代码变成“乐高”状的模块，", 3, "成超",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("大象：Thinking in UML(第2版)", "https://img9.doubanio.com/view/subject/l/public/s27263942.jpg", 8.2, 19.8, "大象：Thinking in UML（第二版），ISBN：9787508492346，作者：谭云杰 著", 5, "魔方",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("算法帝国", "https://img9.doubanio.com/view/subject/l/public/s27277427.jpg", 7.2, 9.6, "人类正在步入与机器共存的科幻世界？看《纽约时报》畅销书作者讲述算法和机器学习技术如何悄然接管人类社会，带我们走进一个算法统治的世界。今天，算法涉足的领域已经远远超出了其创造者的预期。特别是进入信息时代以后，算法的应用涵盖金融、医疗、法律、体育、娱乐、外交、文化、国家安全等诸多方面，显现出源于人类而又超乎人类的强大威力。本书是《纽约时报》畅销书作者的又一力作，通过一个又一个引人入胜的故事，向读者介绍", 4, "安徒生",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Effective Python: 2nd Edition", "https://img9.doubanio.com/view/subject/l/public/s33506797.jpg", 9.2, 47.2, "Updated and Expanded for Python 3It’s easy to start developing programs with Python", 4, "刘宛晴",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Go语言学习笔记", "https://img9.doubanio.com/view/subject/l/public/s28878681.jpg", 8.2, 24.99, "作为时下流行的一种系统编程语言，Go 简单易学，性能很好，且支持各类主流平台。已有大量项目采用 Go 编写，这其中就包括 Docker 等明星作品，其开发和执行效率早已被证明。本书经四年多逐步完善，内容覆盖了语言、运行时、性能优化、工具链等各层面知识。且内容经大量读者反馈和校对，没有明显的缺陷和错误。上卷细致解析了语言规范相关细节，便于读者深入理解语言相关功能的使用方法和注意事项。下卷则对运行时源", 1, "石头",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Go并发编程实战（第2版）", "https://img9.doubanio.com/view/subject/l/public/s29421945.jpg", 7.7, 24.99, "本书首先介绍了Go语言的优秀特性、安装设置方法、工程结构、标准命令和工具、语法基础、数据类型以及流程控制方法，接着阐述了与多进程编程和多线程编程有关的知识，然后重点介绍了goroutine、channel以及Go提供的传统同步方法，最后通过一个完整实例——网络爬虫框架进一步阐述Go语言的哲学和理念，同时分享作者在多年编程生涯中的一些见解和感悟。与上一版相比，本书不仅基于Go 1.8对上一版进行了全", 10, "刘汝佳",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Java并发编程的艺术", "https://img9.doubanio.com/view/subject/l/public/s28275418.jpg", 7.4, 24.99, "并发编程领域的扛鼎之作，作者是阿里和1号店的资深Java技术专家，对并发编程有非常深入的研究，《Java并发编程的艺术》是他们多年一线开发经验的结晶。本书的部分内容在出版早期发表在Java并发编程网和InfoQ等技术社区，得到了非常高的评价。它选取了Java并发编程中最核心的技术进行讲解，从JDK源码、JVM、CPU等多角度全面剖析和讲解了Java并发编程的框架、工具、原理和方法，对Java并发编", 10, "石头",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("大道至易", "https://img9.doubanio.com/view/subject/l/public/s10250079.jpg", 7.0, 1.99, "本书可以看成是《大道至简》的姊妹篇，是以软件工程为体系、以组织结构为视角，融合系统架构师、项目管理者和软件开发人员三种角色实践的思想总成。本书讨论这些思想，并陈述它们所基于的原则、背景与获得过程。本书适合各类工程管理人员、软件开发人员和架构师阅读。作序者对本书的评价：本书作者站在架构师的高度上来看待软件开发，甚至更大的意义上，看待以软件开发能力为核心的企业在产品战略、人力配置和文化方面的组织问题。", 10, "周树人",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("Java核心技术·卷 I（原书第10版）", "https://img9.doubanio.com/view/subject/l/public/s29063065.jpg", 8.3, 12.49, "Java领域最有影响力和价值的著作之一，由拥有20多年教学与研究经验的资深Java技术专家撰写(获Jolt大奖)，与《Java编程思想》齐名，10余年全球畅销不衰，广受好评。第10版根据Java SE 8全面更新，同时修正了第9版中的不足，系统全面讲解了Java语言的核 心概念、语法、重要特性和开发方法，包含大量案例，实践性强。一直以来，《Java核心技术》都被认为是面向高级程序员的经典教程和参考", 7, "刘汝佳",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("现代操作系统（原书第4版）", "https://img9.doubanio.com/view/subject/l/public/s29633301.jpg", 9.1, 25.6, "Andrew S. Tanenbaum教授编写的教材《现代操作系统》现在已经是第4版了。第4版在保持原有特色的基础上，又增添了许多新的内容，反映了当代操作系统的发展与动向，并不断地与时俱进。对比第3版，第4版有很多变化。一些是教材中多处可见的细微变化，一些是就某一功能或机制增加了对最新技术的介绍，如增加了futex同步原语、读–复制–更新（Read-Copy-Update）机制以及6级RAID的内", 9, "成超",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("思考的乐趣", "https://img9.doubanio.com/view/subject/l/public/s10323913.jpg", 8.2, 29.99, "本书是一个疯狂数学爱好者的数学笔记，面向所有喜爱数学的读者。从2005年7月开始，作者已经写了连续六年的博客，积累下来了大量的数学文章。 部分文章内容被广泛关注，在网络上大量分享转载。这本书有意挑选了初等的话题，让大大小小的读者都能没有障碍地阅读。文章内容新，让有数学背景的人也会发现很多自己没见过的初等问题。 文章是独立的。一篇文章一个话题，文章与文章之间基本不会做参考，读者可以随意跳着看。文章内", 1, "猪猪",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("科学的极致：漫谈人工智能", "https://img9.doubanio.com/view/subject/l/public/s28231109.jpg", 8.1, 11.99, "集智俱乐部是一个从事学术研究、享受科学乐趣的探索者组成的团体，倡导以平等开放的态度、科学实证的精神进行跨学科的研究与交流，力图搭建一个中国的“没有围墙的研究所”。这些令人崇敬的、充满激情与梦想的集智俱乐部成员将带你了解图灵机模型、冯•诺依曼计算机体系结构、怪圈与哥德尔定理、通用人工智能、深度学习、人类计算与自然语言处理，与你一起展开一场令人热血沸腾的科学之旅。", 9, "鲁迅",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("代码整洁之道:程序员的职业素养", "https://img9.doubanio.com/view/subject/l/public/s29376034.jpg", 8.8, 22.99, "1. 汇聚编程大师40余年编程生涯的心得体会2. 阐释软件工艺中的原理、技术、工具和实践3. 助力专业软件开发人员具备令人敬佩的职业素养成功的程序员在以往的工作和生活中都曾经历过大大小小的不确定性，承受过永无休止的压力。他们之所以能够成功，是因为拥有一个共同点，都深切关注创建软件所需的各项实践。他们将软件开发视为一种需要精雕细琢加以修炼的技艺，他们以专业人士的标准要求自己，他们具有职业素养。软件开", 5, "李大钊",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("游戏人工智能", "https://img9.doubanio.com/view/subject/l/public/s29559214.jpg", 8.5, 39.99, "游戏中的人工智能（AI）开发是游戏研发中最具挑战性的任务之一，而本书汇集了与游戏人工智能开发有关的知识，借助这些知识，程序员能够开发出有趣的AI 角色，为玩家带来美妙的游戏体验。本书分为7 个部分共48 章，分别讨论了通用智能、体系架构、运动与寻路、战略与战术、agent 意识与知识表征、竞速以及其他领域的游戏AI 技术。读者可以以本书作为路线图，了解在智能游戏方面哪些技术已经被成功使用，哪些具有", 4, "马丽芳",100);
insert into rented_book_inventory(book_name, icon, rating, price, description, category_id, author,amount) values("构建之法", "https://img9.doubanio.com/view/subject/l/public/s27426301.jpg", 8.7, 24.99, "内容简介：软件工程牵涉的范围很广", 2, "王伟",100);
